sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var color = gs.getProperty("x_165694_time_of_0.u_header_bgcolor");
    var icon = gs.getProperty("x_165694_time_of_0.u_company_icon");
    var imgGr = new GlideRecord("db_image");
	imgGr.addQuery('name',icon);
	imgGr.query();
	if(!imgGr.next()){
		icon = null;
	}
	var timeOff = new TimeOffSNC;
	var checkHex = timeOff.checkHex(color);
	if(!color || !checkHex){
		color = '#151920';
	}
    template.print('<div style="width: 600px; height: 210px; background-color:' + color + ';">');
    if (icon) {
        template.print('<img style="align: middle;" title="" src="' + icon + '" alt="" width="94.16" height="64" align="middle" border="" hspace="250" vspace="60" />');
    } else {
        template.print('<h1 style="padding-left: 2rem;padding-right: 2rem;font-family: Open Sans, Arial, sans-serif;text-align: center; color: #FFFFFF; word-wrap: break-word; padding-top: 4rem; display: -webkit-box;-webkit-line-clamp: 2;-webkit-box-orient: vertical;overflow: hidden;">Time Off Tracker<h1>');
    }
    template.print('</div>');


})(current, template, email, email_action, event);
